import React from "react";
import {
	Col
} from "reactstrap";

import { 
	Typography ,
	Tabs,
	Tab,
	Box,
} from '@material-ui/core/';
import TabPanel from 'components/General/TabPanel.js';

function CompanyTab(props) {

	const [value, setValue] = React.useState(0);

	const handleChange = (event, newValue) => {
		setValue(newValue);
	};

	const { company, description, papeles, obligaciones } = props.informe;
	return (
		<div style={{marginLeft: '5%'}}>
			<h4 style={{marginTop: 0}}>{company}</h4>
			<p className="text">{description}</p>
			<Tabs
				value={value}
				indicatorColor="primary"
				textColor="primary"
				onChange={handleChange}
				aria-label="disabled tabs example"
			>
				<Tab label="Papeles Comerciales" />
				<Tab label="Obligaciones a largo plazo" />
			</Tabs>
			<TabPanel value={value} index={0}>{papeles.map(papel => <h5>{papel}</h5>)}</TabPanel>
			<TabPanel value={value} index={1}>{obligaciones.map(obligacion => <p>{obligacion}</p>)}</TabPanel>
		</div>
	);
}

export { CompanyTab };
