import React from "react";

import { 
  List, 
  ListItem, 
  Card, 
  CardActionArea, 
  CardActions, 
  CardContent, 
  CardMedia, 
  GridList,
  Typography 
} from '@material-ui/core/';
import { makeStyles } from '@material-ui/core/styles';

import {
  Container,
  Row,
} from "reactstrap";


const useStyles = makeStyles({
  root: {
    width: 170,
    height: 67,
  },
  media: {
    width: '100%',
    height: '100%',
  },
  gridList: {
    flexWrap: 'nowrap',
    // Promote the list into his own layer on Chrome. This cost memory but helps keeping high FPS.
    transform: 'translateZ(0)',
  },
});

function MediaCard(props) {
  const classes = useStyles();
  console.log(props.image);
  return (
      <CardActionArea className={classes.root}>
        <CardMedia
          className={classes.media}
          image={props.image}
          title={props.name}
        />
      </CardActionArea>
  );
}
//
const clients = [ 
  {
    fullName: "PROCESADORA NACIONAL DE ALIMENTOS PRONACA C.A.",
    shortName: "Pronaca",
    logo: require("assets/img/Pronaca.png"),
  },
  {
    fullName: "Pinturas Condor",
    shortName: "Condor",
    logo: require("assets/img/Condor.png"),
  },
  {
    fullName: "Unifer",
    shortName: "Unifer",
    logo: require("assets/img/Unifer.png"),
  },
  {
    fullName: "Produbanco",
    shortName: "Produbanco",
    logo: require("assets/img/Produbanco.png"),
  },
  {
    fullName: "MUNDO DEPORTIVO MEDEPORT",
    shortName: "Medeport",
    logo: require("assets/img/Medeport.png"),
  },
];



class ClientCarousel extends React.Component {
  render() {

    return (
      <div className={{
    display: 'flex',
    flexWrap: 'wrap',
    justifyContent: 'space-around',
    overflow: 'hidden',
  }}>
          <GridList className={{
    flexWrap: 'nowrap',
    // Promote the list into his own layer on Chrome. This cost memory but helps keeping high FPS.
    transform: 'translateZ(0)',
  }} cols={8}>
            {clients.map(client => (<ListItem> <MediaCard image={client.logo} primaryText={client.shortName} /> </ListItem>))}
          </GridList> 
      </div>
  );
  }
}


export default ClientCarousel;
