import React, { useState } from "react";

// reactstrap components
import {
  Button,
  NavItem,
  NavLink,
  Nav,
  TabContent,
  TabPane,
  Container,
  Row,
  Col,
  UncontrolledTooltip,
  Card, 
  CardText, 
  CardBody,
  CardTitle, 
  CardSubtitle, 
  Collapse,
} from "reactstrap";


import ExamplesNavbar from "components/Navbars/ExamplesNavbar.js";
import GenericHeader from "components/Headers/GenericHeader.js";
import DefaultFooter from "components/Footers/DefaultFooter.js";


const CollapsedQuestion = ({question, answer} : props) => {

  const [isOpen, setIsOpen] = useState(false);
  const toggle = () => setIsOpen(!isOpen);

  return( 
    <div>
      <Button onClick={toggle} style={{ marginBottom: '1rem' }}>{question}</Button>
      <Collapse isOpen={isOpen}>
        <Card>
          <CardBody>{answer}</CardBody>
        </Card>
      </Collapse>
    </div>
  );
};
//

const steps =
    [
      {name: 'Step 1', component: <p>Hello</p>},
      {name: 'Step 2', component: <p>Hello</p>},
];
//

function ProductPage(props) {

  const headerImage = "url(" + require("assets/img/bg4.jpg") + ")";
  React.useEffect(() => {
    document.body.classList.add("profile-page");
    document.body.classList.add("sidebar-collapse");
    document.documentElement.classList.remove("nav-open");
    return function cleanup() {
      document.body.classList.remove("profile-page");
      document.body.classList.remove("sidebar-collapse");
    };
  });
  const { contract } = {};
  return (
    <>
      <ExamplesNavbar />
      <div className="wrapper">
        <GenericHeader image={headerImage} title="La Plataforma" subtitle="Modernizamos los reportes financieros"/>
          <Container>
            <Row>  

            </Row>
          </Container>
        <DefaultFooter />
      </div>
    </>
  );
}

export default ProductPage;
