import React from "react";

import { 
  List, 
  ListItem, 
  Card, 
  CardActionArea, 
  CardActions, 
  CardContent, 
  CardMedia, 
  Typography 
} from '@material-ui/core/';
import { makeStyles } from '@material-ui/core/styles';

import {
  Container,
  Row,
} from "reactstrap";


const useStyles = makeStyles({
  root: {
    paddingLeft: '5%',
    paddingRight: '5%',

  },
  media: {
    width: '100%',
    height: '100%',
  },
});

function MediaCard(props) {
  const classes = useStyles();
  console.log(props.image);
  return (
      <CardActionArea className={classes.root}>
        <CardMedia
          className={classes.media}
          image={props.image}
          title={props.name}
        />
      </CardActionArea>
  );
}
//
const clients = [ 
  {
    fullName: "PROCESADORA NACIONAL DE ALIMENTOS PRONACA C.A.",
    shortName: "Pronaca",
    logo: require("assets/img/Pronaca.png"),
  },
  {
    fullName: "Pinturas Condor",
    shortName: "Condor",
    logo: require("assets/img/Condor.png"),
  },
  {
    fullName: "Unifer",
    shortName: "Unifer",
    logo: require("assets/img/Unifer.png"),
  },
  {
    fullName: "Produbanco",
    shortName: "Produbanco",
    logo: require("assets/img/Produbanco.png"),
  },
  {
    fullName: "MUNDO DEPORTIVO MEDEPORT",
    shortName: "Medeport",
    logo: require("assets/img/Medeport.png"),
  },
];



class ClientCarousel extends React.Component {
  render() {
    return (
      <div style={{
        display: 'flex',
            alignItemcs: 'center',
          justifyContent: 'center'
          }}>
        <List style={{
            display: 'flex',
            flexDirection: 'row',
            maxWidth: '80%',
          }}>
          {clients.map(client => (<ListItem style={{paddingLeft: 20, paddingRight: 20,}}> <MediaCard image={client.logo} primaryText={client.shortName} /> </ListItem>))}
        </List> 
      </div>

  );
  }
}


export default ClientCarousel;
