'use strict';

import React from 'react';
import PersonalInformationSection from './PersonalInformationSection';

import ReactPaginate from 'react-paginate';

import './main.css';

//https://stackoverflow.com/questions/58740073/react-hooks-at-the-top-level-of-a-multi-page-form-built-with-react-hook-form
//https://react-hook-form.com/advanced-usage#WizardFormFunnel

class BasicHook extends React.Component {

  render() {
    const { getStore, updateStore, name } = this.props;
    return( 
      <div>
      <p>{name}</p>
      <PersonalInformationSection getStore={() => (this.getStore())} updateStore={(u) => {this.updateStore(u)}}/>
      </div>
    );
  }
};
//

export default class MainForm extends React.Component {
  constructor(props) {
    super(props);
     this.state = {
      data: [],
      selected: 0,
    };
    this.sampleStore = {
      menor: {
      },
      madre: {
      },
      padre: {},
    };
  }

  componentDidMount() {}

  componentWillUnmount() {}

  getStore(key) {
    return this.sampleStore[key];
  }

  updateStore(key, update) {
    this.sampleStore[key] = {
      ...update,
    }
    console.log('in update store');
    console.log(this.sampleStore);
  }

  handlePageClick = data => {
    let selected = data.selected;
    this.setState({ selected });
  };

  render() {
    const steps =
    [
      {name: 'Informacion de la/el Menor', component: <PersonalInformationSection sectionName="menor" getStore={() => (this.getStore("menor"))} updateStore={(u) => {this.updateStore("menor", u)}}/>},
      {name: 'Informacion de la Madre', component: <PersonalInformationSection sectionName="madre" getStore={() => (this.getStore("madre"))} updateStore={(u) => {this.updateStore("madre", u)}}/>},
      {name: 'Informacion del Padre', component: <PersonalInformationSection sectionName="padre" getStore={() => (this.getStore("padre"))} updateStore={(u) => {this.updateStore("padre", u)}}/>},
    ]

    return (

      <div className="example">
        <h2> {steps[this.state.selected].name}</h2>
        <div className="step-progress">
          {steps[this.state.selected].component}
        </div>
        <ReactPaginate
          previousLabel={'Anterior'}
          nextLabel={'Siguiente'}
          breakLabel={'...'}
          breakClassName={'break-me'}
          pageCount={3}
          onPageChange={this.handlePageClick}
          containerClassName={'pagination'}
          subContainerClassName={'pages pagination'}
          activeClassName={'active'}
        />
      </div>
    );
  }
}
