import React from 'react';
import { makeStyles } from '@material-ui/core/styles';


import { 
  Typography ,
  Grid,
  Paper,
  Card,
  CardActionArea,
  CardActions,
  CardContent,
  CardMedia,
  Button,
} from '@material-ui/core/';

import { Person, AccessTime } from '@material-ui/icons';

const useStyles = makeStyles({
  root: {
    maxWidth: 345,
  },
  media: {
    height: 140,
  },
});

export default function NewsCard(props) {
  const classes = useStyles();

  const { title, preview, image, author, date } = props;
  return (
    <Card className={classes.root}>
      <CardActionArea>
        <CardMedia
          className={classes.media}
          image={require("assets/img/DefaultNews.jpg")}
          title="Contemplative Reptile"
        />
        <CardContent>
          <Typography gutterBottom variant="h5" component="h2">{title}</Typography>
          <Typography variant="body2" color="textSecondary" component="p">{preview}</Typography>
        <Grid container direction="row" alignItems="center">
          <Grid item>
            <Person />
          </Grid>
          <Grid item style={{marginLeft: 4}}>
            {author}
          </Grid>
          <Grid item>
            <AccessTime />
          </Grid>
          <Grid item>
            {date}
          </Grid>
        </Grid>
        </CardContent>

      </CardActionArea>
    </Card>
  );
}
