import React, { useState } from "react";

// reactstrap components
import {
  Button,
  NavItem,
  NavLink,
  Nav,
  TabContent,
  TabPane,
  Container,
  Row,
  Col,
  UncontrolledTooltip,
  Card, 
  CardText, 
  CardBody,
  CardTitle, 
  CardSubtitle, 
  Collapse,
} from "reactstrap";


import ExamplesNavbar from "components/Navbars/ExamplesNavbar.js";
import ContractPageHeader from "components/Headers/ContractPageHeader.js";
import DefaultFooter from "components/Footers/DefaultFooter.js";


const CollapsedQuestion = ({question, answer} : props) => {

  const [isOpen, setIsOpen] = useState(false);
  const toggle = () => setIsOpen(!isOpen);

  return( 
    <div>
      <Button onClick={toggle} style={{ marginBottom: '1rem' }}>{question}</Button>
      <Collapse isOpen={isOpen}>
        <Card>
          <CardBody>{answer}</CardBody>
        </Card>
      </Collapse>
    </div>
  );
};
//

const steps =
    [
      {name: 'Step 1', component: <p>Hello</p>},
      {name: 'Step 2', component: <p>Hello</p>},
];
//

function ProductPage(props) {

  React.useEffect(() => {
    document.body.classList.add("profile-page");
    document.body.classList.add("sidebar-collapse");
    document.documentElement.classList.remove("nav-open");
    return function cleanup() {
      document.body.classList.remove("profile-page");
      document.body.classList.remove("sidebar-collapse");
    };
  });
  const { contract } = props.location.state;
  return (
    <>
      <ExamplesNavbar />
      <div className="wrapper">
        <ContractPageHeader contract={contract} />
          <Container>
            <Row>  
              <Col className="ml-auto mr-auto" xs="7">
                <h4 className="title text-left">Lo BÃ¡sico Sobre un {contract.name}</h4>
                <p>{contract.description}</p>
                {contract.faq?.map(q => CollapsedQuestion(q))}
              </Col>
              <Col className="ml-auto mr-auto" xs="3" style={{backgroundColor: '#f2f2f2',}}>
                <h5 className="title text-center">Datos Necesarios</h5>
                <ul>
                {contract.requiredInformation?.map(info => <li style={{marginTop: '8%'}}>{info.title} <ul>{info.subtitles.map(st => <li>{st}</li>)}</ul></li>)}
                </ul>
              </Col>
            </Row>
          </Container>
        <DefaultFooter />
      </div>
    </>
  );
}

export default ProductPage;
