import React from "react";
import {
	Col
} from "reactstrap";

import { 
	Typography ,
	Tabs,
	Tab,
	Box,
} from '@material-ui/core/';

function CompanyTab(props) {

	const [value, setValue] = React.useState(0);

	const handleChange = (event, newValue) => {
		setValue(newValue);
	};

	const { company, description, papeles, obligaciones } = props.informe;
	return (
		<div>
			<h2>{company}</h2>
			<h5 className="text">{description}</h5>
			<Tabs
				value={value}
				indicatorColor="primary"
				textColor="primary"
				onChange={handleChange}
				aria-label="disabled tabs example"
			>
				<Tab label="Papeles Comerciales" />
				<Tab label="Obligaciones a largo plazo" />
			</Tabs>
			<TabPanel>{papeles.map(papel => <h5>{papel}</h5>)}</TabPanel>
		</div>
	);
}

export { CompanyTab };
