import React, { useEffect } from 'react';
import { useForm, Controller, ErrorMessage } from 'react-hook-form';
import { TextField } from '@material-ui/core';
import * as yup from "yup";

const schema = yup.object().shape({
  firstName: yup.string().required('Campo obligatorio'),
  lastName: yup.string().required('Campo obligatorio'),
  idNumber: yup.string().required('Campo obligatorio'),
});

export default function PersonalInformationSection(props) {
  console.log("rendering");
  console.log(props.sectionName);
  const { firstName, lastName, idNumber } = props.getStore();

  useEffect(() => {
    //console.log("mounteeeeed");

    // code to run on component mount
  }, [])

  console.log(lastName);
  const { register, handleSubmit, errors, control, setValue, reset } = useForm({
    validationSchema: schema,
    defaultValues: { firstName, lastName, idNumber },
  });

  const onSubmit = data => {
    //console.log(props.values);
    //console.log('resettinggg!!!')
    props.updateStore({
      ...data,
    });    
    reset({ firstName: "", lastName: "", idNumber: "" });
  }

  const controlledComponent = (name, label) => { 
    return (
      <div>
        <Controller
            as={<TextField error={errors[name]}/>}
            name={name}
            label={label}
            control={control}
            defaultValue=""
            rules={schema.firstName}
        />
        <ErrorMessage errors={errors} name={name}>
          {({ message }) => <p style={{color: 'red', fontSize: 12}}>{message}</p>}
        </ErrorMessage>
      </div>
  );
}
  //


  return (
    <form onSubmit={handleSubmit(onSubmit)}>
      <div style={{display: 'flex', justifyContent: 'space-between'}}>
        {controlledComponent("firstName", "Nombre")}
        {controlledComponent("lastName", "Apellido")}
        {controlledComponent("idNumber", "Cedula de identidad")}
      </div>
      <div style={{display: 'flex', justifyContent: 'space-between'}}>
        <input type="submit" value="Anterior"/>
        <input type="submit" value="Siguiente"/>
      </div>
    </form>
  );
}
