import React from "react";

// reactstrap components
import {
  Button,
  Input,
  InputGroupAddon,
  InputGroupText,
  InputGroup,
  Container,
  Row,
  Col
} from "reactstrap";


function TeamMember(props) {
  return (
    <Col md="4">
      <div className="team-player">
        <img
          alt="..."
          className="rounded-circle img-fluid img-raised"
          src={props.image}
        ></img>
        <h4 className="title">{props.name}</h4>
        <p className="category text-info">{props.title}</p>
        <p className="description">
          Doctor en Jurisprudencia, Abogado, Especializado en Derecho Financiero y Empresarial. 
          Miembro Vocal de la Rectoría de la Bolsa de Valores de Quito.
        </p>

        <Button
          className="btn-icon btn-round"
          color="info"
          onClick={e => window.open("https://www.linkedin.com/in/eduardo-aguirre-b1950248/", '_blank')}
        >
          <i className="fab fa-linkedin"></i>
        </Button>
      </div>
    </Col>

  );
}

const teamMembers = [
  {
    name: "Eduardo Aguirre",
    title: "Abogado",
    bio: "Doctor en Jurisprudencia, Abogado, Especializado en Derecho Financiero y Empresarial. \
          Miembro Vocal de la Rectoría de la Bolsa de Valores de Quito.",
    headhsot: require("assets/img/eduardo.jpg"),
  },
  {
    name: "Dayana Miranda",
    title: "Asesora Financiera",
    bio: "Doctor en Jurisprudencia, Abogado, Especializado en Derecho Financiero y Empresarial. \
          Miembro Vocal de la Rectoría de la Bolsa de Valores de Quito.",
    headhsot: require("assets/img/dayana.jpg"),
  },
  {
    name: "Jorge Eguiguren",
    title: "Ingeniero",
    bio: "Estudiante de ingeniería decidido a programar hasta que el acceso a la educación \
                      superior en el Ecuador esté garantizada a todo estudiante. ",
    headhsot: require("assets/img/jorge.jpg"),
  },
];


// core components
function OurTeam() {
  React.useEffect(() => {
    document.body.classList.add("landing-page");
    document.body.classList.add("sidebar-collapse");
    document.documentElement.classList.remove("nav-open");
    return function cleanup() {
      document.body.classList.remove("landing-page");
      document.body.classList.remove("sidebar-collapse");
    };
  });
  return (
        <div className="section section-team text-center">
          <Container>
            <h2 className="title">Nuestro Equipo</h2>
            <div className="team">
              <Row>
                {teamMembers.map(member => <TeamMember member={member}/>)}
              </Row>
            </div>
          </Container>
          </div>
  );
}

export default OurTeam;
