import React from 'react';
import { makeStyles } from '@material-ui/core/styles';


import { 
  Typography ,
  Grid,
  Paper,
  Card,
  CardActionArea,
  CardActions,
  CardContent,
  CardMedia,
  Button,
} from '@material-ui/core/';

import { Person, AccessTime } from '@material-ui/icons';

const useStyles = makeStyles({
  root: {

  },
  media: {
    minWidth: '30%',
    height: 200,
  },
  content: {
    minWidth: '70%',
    height: 200,
  },
});

export default function NewsCard(props) {
  const classes = useStyles();

  const { title, preview, image, author, date } = props;
  return (
    <Card className={classes.root} style={{width: '100%'}}>
      <CardActionArea style={{width: '100%'}}>
        <Grid container direction="row" alignItems="left" spacing={4} style={{width: '100%', backgroundColor: 'red'}}>
          <Grid item>
            <CardMedia
              className={classes.media}
              image={require("assets/img/DefaultNews.jpg")}
              title="Contemplative Reptile"
            />
          </Grid>
          <Grid item>
            <CardContent className={classes.content}>
              <Typography gutterBottom variant="h5" component="h2">{title}</Typography>
              <Typography variant="body2" color="textSecondary" component="p">{preview}</Typography>
              <Grid container direction="row" alignItems="left" spacing={4} style={{marginTop: 4}}>
                <Grid item>
                  <Grid container direction="row" alignItems="center" spacing={1}>
                    <Grid item>
                      <Person />
                    </Grid>
                    <Grid item>
                      {author}
                    </Grid>
                  </Grid>
                </Grid>
                <Grid item>
                  <Grid container direction="row" alignItems="center" spacing={1}>
                    <Grid item>
                      <AccessTime />
                    </Grid>
                    <Grid item>
                      {date}
                    </Grid>
                  </Grid>
                </Grid>
              </Grid>
            </CardContent>
          </Grid>
        </Grid>
      </CardActionArea>
    </Card>
  );
}
