import React from "react";

import { 
  List, 
  ListItem, 
  Card, 
  CardActionArea, 
  CardActions, 
  CardContent, 
  CardMedia, 
  Typography 
} from '@material-ui/core/';
import { makeStyles } from '@material-ui/core/styles';

import {
  Container,
  Row,
} from "reactstrap";


const useStyles = makeStyles({
  root: {
    width: 230,
  },
  media: {
    height: 110,
  },
});

function MediaCard(props) {
  const classes = useStyles();
  console.log(props.image);
  return (
    <Card className={classes.root}>
      <CardActionArea>
        <CardMedia
          className={classes.media}
          image={props.image}
          title={props.name}
        />
      </CardActionArea>
    </Card>
  );
}

const clients = [ 
  {
    fullName: "PROCESADORA NACIONAL DE ALIMENTOS PRONACA C.A.",
    shortName: "Pronaca",
    logo: require("assets/img/Pronaca.png"),
  },
  {
    fullName: "Pinturas Condor",
    shortName: "Condor",
    logo: require("assets/img/Condor.png"),
  },
  {
    fullName: "Plasticos Rival",
    shortName: "Rival",
    logo: require("assets/img/Rival.png"),
  },
  {
    fullName: "Unifer",
    shortName: "Unifer",
    logo: require("assets/img/Unifer.png"),
  },
  {
    fullName: "Produbanco",
    shortName: "Produbanco",
    logo: require("assets/img/Produbanco.png"),
  },
  {
    fullName: "MUNDO DEPORTIVO MEDEPORT",
    shortName: "Medeport",
    logo: require("assets/img/Medeport.png"),
  },
];



class ClientCarousel extends React.Component {
  render() {
    return (
      <Container>
        <Row>
          <List style={{
              display: 'flex',
              flexDirection: 'row',
            }}>
            {clients.map(client => (<ListItem> <MediaCard image={client.logo} primaryText={client.shortName} /> </ListItem>))}
          </List> 
        </Row>
      </Container>
  );
  }
}


export default ClientCarousel;
