import React from "react";
import { Link } from 'react-router-dom'

// reactstrap components
import {
  Button,
  NavItem,
  NavLink,
  Nav,
  TabContent,
  TabPane,
  Container,
  Row,
  Col,
  UncontrolledTooltip,
  Card, 
  CardText, 
  CardBody,
  CardTitle, 
  CardSubtitle, 
} from "reactstrap";

// core components
import ExamplesNavbar from "components/Navbars/ExamplesNavbar.js";
import AboutPageHeader from "components/Headers/AboutPageHeader.js";
import DefaultFooter from "components/Footers/DefaultFooter.js";


const ContractCard = ({contract} : props) => {
  return (
     <div style={{width: '30%', }}>
      <Card style={{ height: '90%',}}>
        <CardBody>
          <CardTitle>{contract.name}</CardTitle>
          <CardSubtitle>${contract.price}</CardSubtitle>
          <CardText style={{fontSize: 14}}>{contract.description}</CardText>
          <Link to={{
              pathname: 'contract-page',
              state: {
                contract: contract
              }
            }}>
            <Button>Empezar</Button>
          </Link>
        </CardBody>
      </Card>
    </div>
  );
}
//
const faq = [
      {
        question: 'Que es un contrato de alquiler?',
        answer: 'El contrato de alquiler es el que recae sobre una edificación habitable y cuyo destino sea satisfacer la necesidad del inquilino. La habitabilidad significa que tenga unas condiciones mínimas de salubridad e higiene aptas para el desarrollo de la vida normal.',
      }, 
      {
        question: 'Cuanto tiempo dura un contrato de alquiler?',
        answer: 'Aunque puedes pactar que el contrato tenga una duración de 1 año o incluso que dure menos tiempo, la ley establece como plazo mínimo un plazo de 2 años.',
      }, 
      {
        question: 'Porque necesito un contrato de alquiler?',
        answer: 'Necesitarás este contrato si quieres alquilar un inmueble de tu propiedad para que lo use otra persona.',
      }, 
      {
        question: 'Que cubre este contrato?',
        answer: 'El alquiler con o sin mobiliario, el régimen mínimo de duración del alquiler, el Precio, su actualización y demás conceptos asimilados al precio y los gastos generales y de servicios individuales',
      }, 
    ];

const requiredInformation = [
  {
    title: 'Propietario e Inquilino',
    subtitles: ['Nombre y apellido o razón social', 'Cédula de identidad', 'Domiciolio']
  },
  {
    title: 'Inmueble',
    subtitles: ['Domiciolio', 'Unidad / No. de Apartamento']
  },
  {
    title: 'Precio y forma de pago',
    subtitles: []
  },
  {
    title: 'Garante (opcional)',
    subtitles: ['Nombre y apellido o razón social', 'Cédula de identidad', 'Domiciolio']
  },
];

const popularContracts = [
  {
    name:  "Permiso de Salida del País", 
    price: 400, 
    description: "Para los niños y niñas, y adolescentes ecuatorianos y extranjeros residentes en el país, se necesita permiso de salida de los padres o del padre que no viaja con el menor.",
    faq: faq,
    requiredInformation: requiredInformation,
  },
  {
    name:  "Contrato de Alquiler", 
    price: 500, 
    description: "Es un contrato que se realiza cuando se alquila una propiedad inmueble, por un tiempo determinado y para fines determinados, estableciendo el monto de pago por el uso de dicha propiedad.",
    faq: faq,
    requiredInformation: requiredInformation,
  },
  {
    name:  "Contrato de Confidencialidad", 
    price: 600, 
    description: "Es un contrato en el cual guardar secreto y confidencialidad sobre cierta información que se va a compartir, y a usarla sólo para el fin que se estipule.",
    faq: faq,
    requiredInformation: requiredInformation,
  },
];  

function AboutPage() {
  const [pills, setPills] = React.useState("2");
  React.useEffect(() => {
    document.body.classList.add("profile-page");
    document.body.classList.add("sidebar-collapse");
    document.documentElement.classList.remove("nav-open");
    return function cleanup() {
      document.body.classList.remove("profile-page");
      document.body.classList.remove("sidebar-collapse");
    };
  });
  return (
    <>
      <ExamplesNavbar />
      <div className="wrapper">
        <AboutPageHeader />
          <Container>
            <Row>  
              <Col className="ml-auto mr-auto" md="16">
                <h4 className="title text-center">Documentos Populares</h4>
                <div style={{ display: 'flex',
                              alignItems: 'space-between',
                              justifyContent: 'space-between',
                             }}>

                  {popularContracts.map(contract => <ContractCard contract={contract}/>)}

                </div>
              </Col>
            </Row>
          </Container>
        <DefaultFooter />
      </div>
    </>
  );
}

export default AboutPage;
