import React from "react";

import { 
  List, 
  ListItem, 
  Card, 
  CardActionArea, 
  CardActions, 
  CardContent, 
  CardMedia, 
  Typography ,
  Grid,
} from '@material-ui/core/';
import { makeStyles } from '@material-ui/core/styles';

import {
  Container,
  Row,
  Image,
} from "reactstrap";


const useStyles = makeStyles({
  root: {
    width: 150,
    height: 55,
  },
  media: {
    width: '100%',
    height: '100%',
  },
});

function MediaCard(props) {
  const classes = useStyles();
  console.log(props.image);
  return (
      <CardActionArea className={classes.root}>
        <CardMedia
          className={classes.media}
          image={props.image}
          title={props.name}
        />
      </CardActionArea>
  );
}
//
const clients = [ 
  {
    fullName: "PROCESADORA NACIONAL DE ALIMENTOS PRONACA C.A.",
    shortName: "Pronaca",
    logo: require("assets/img/Pronaca.png"),
  },
  {
    fullName: "Pinturas Condor",
    shortName: "Condor",
    logo: require("assets/img/Condor.png"),
  },
  {
    fullName: "Unifer",
    shortName: "Unifer",
    logo: require("assets/img/Unifer.png"),
  },
  {
    fullName: "Produbanco",
    shortName: "Produbanco",
    logo: require("assets/img/Produbanco.png"),
  },
  {
    fullName: "MUNDO DEPORTIVO MEDEPORT",
    shortName: "Medeport",
    logo: require("assets/img/Medeport.png"),
  },
];



class ClientCarousel extends React.Component {
  render() {
    return (
      <>
      <Grid 
        container
        direction="row"
        justify="space-around"
        alignItems="center">
          {clients.map(client => {
            console.log(client);
            return (
            <Grid item xs>
              <Image
              src={client.logo}
              title={client.name}
            />
            </Grid>);
          }
            )}
      </Grid>
      <div style={{
        display: 'flex',
            alignItemcs: 'center',
          justifyContent: 'center'
          }}>
        <List style={{
            display: 'flex',
            flexDirection: 'row',
          }}>
          {clients.map(client => (<ListItem style={{paddingLeft: 40, paddingRight: 40,}}> <MediaCard image={client.logo} primaryText={client.shortName} /> </ListItem>))}
        </List> 
      </div>  
      </>  
    );
  }
}

export default ClientCarousel;
