import React from "react";

import { 
  List, 
  ListItem, 
  Card, 
  CardActionArea, 
  CardActions, 
  CardContent, 
  CardMedia, 
  Typography 
} from '@material-ui/core/';
import { makeStyles } from '@material-ui/core/styles';

import {
  Container,
  Row,
} from "reactstrap";


const useStyles = makeStyles({
  root: {
    maxWidth: 345,
  },
  media: {
    height: 140,
  },
});

function MediaCard(props) {
  const classes = useStyles();
  console.log(props);
  return (
    <Card className={classes.root}>
      <CardActionArea>
        <CardMedia
          className={classes.media}
          image={props.image}
          title="Contemplative Reptile"
        />
        <CardContent>
          <Typography gutterBottom variant="h5" component="h2">
            {props.primaryText}
          </Typography>
        </CardContent>
      </CardActionArea>
    </Card>
  );
}

const clients = [ 
  {
    fullName: "PROCESADORA NACIONAL DE ALIMENTOS PRONACA C.A.",
    shortName: "Pronaca",
    logo: "assets/img/bg5.jpg",
  },
  {
    fullName: "PROCESADORA NACIONAL DE ALIMENTOS PRONACA C.A.",
    shortName: "Produbanco",
    logo: "assets/img/bg5.jpg",
  },
  {
    fullName: "PROCESADORA NACIONAL DE ALIMENTOS PRONACA C.A.",
    shortName: "Plasticos Rival",
    logo: "assets/img/bg5.jpg",
  },
  {
    fullName: "PROCESADORA NACIONAL DE ALIMENTOS PRONACA C.A.",
    shortName: "Unifer",
    logo: "assets/img/bg5.jpg",
  },
  {
    fullName: "PROCESADORA NACIONAL DE ALIMENTOS PRONACA C.A.",
    shortName: "Industrias Ales",
    logo: "assets/img/bg5.jpg",
  },
];



class ClientCarousel extends React.Component {
  render() {
    return (
      <Container>
        <Row>
          <List style={{
              display: 'flex',
              flexDirection: 'row',
              padding: 0,
            }}>
            {clients.map(client => (<ListItem> <MediaCard image={client.logo} primaryText={client.shortName} /> </ListItem>))}
          </List> 
        </Row>
      </Container>
  );
  }
}


export default ClientCarousel;
